batch_num=2; truncate -s 0 baylor_batch_${batch_num}/baylor_batch_${batch_num}_FastQC_R2.warnings; for file in baylor_batch_${batch_num}/*/fastqc; do echo "${file}"; perl -ne 'while ($_ =~ /WARNING]"\/\>\<a href=\"\#M\d+\"\>([a-zA-Z\s]+)\<\/a\>\<\/li\>/ig) { print "$1\n"}' ${file}/*_R2_001_fastqc.html >> baylor_batch_${batch_num}/baylor_batch_${batch_num}_FastQC_R2.warnings;  done ; sort baylor_batch_${batch_num}/baylor_batch_${batch_num}_FastQC_R2.warnings | uniq -c > baylor_batch_${batch_num}/baylor_batch_${batch_num}_FastQC_R2.warnings.stats; samples_num=`wc -l baylor_batch_${batch_num}/SAMPLENAMES.list | awk '{print $1}'`; echo "     ${samples_num} total number of samples" >> baylor_batch_${batch_num}/baylor_batch_${batch_num}_FastQC_R2.warnings.stat
